import{d as t,c as e,b as s,h as d,o,_ as i,g as n}from"./index-6f86f1ea.js";const l={class:"min-h-screen"},r=t({__name:"Documentation",setup(c){return(v,a)=>(o(),e("div",l,[s(i),a[0]||(a[0]=d('<main class="container mx-auto px-6 py-8 max-w-4xl" data-v-77759f09><div class="main-container p-8" data-v-77759f09><div class="text-center mb-8" data-v-77759f09><h1 class="text-4xl font-bold text-theme-primary mb-4" data-v-77759f09>Documentation</h1><p class="text-lg text-theme-secondary" data-v-77759f09>Learn how to use the Neural Network Playground</p></div><div class="prose prose-lg max-w-none" data-v-77759f09><section class="mb-8" data-v-77759f09><h2 class="text-2xl font-bold text-theme-primary mb-4" data-v-77759f09>Getting Started</h2><p class="mb-4" data-v-77759f09> The Neural Network Playground allows you to visualize and interact with neural networks in real-time. Here&#39;s how to get started: </p><ol class="space-y-2" data-v-77759f09><li data-v-77759f09><strong data-v-77759f09>Upload Data:</strong> Click &quot;Choose CSV File&quot; and select a CSV file with x, y, label columns</li><li data-v-77759f09><strong data-v-77759f09>Add Neurons:</strong> Click anywhere on the canvas to add neurons</li><li data-v-77759f09><strong data-v-77759f09>Configure Settings:</strong> Choose similarity metrics and activation functions</li><li data-v-77759f09><strong data-v-77759f09>Optimize:</strong> Run gradient descent to train your network</li><li data-v-77759f09><strong data-v-77759f09>Analyze:</strong> View metrics and loss landscapes in real-time</li></ol></section><section class="mb-8" data-v-77759f09><h2 class="text-2xl font-bold text-theme-primary mb-4" data-v-77759f09>Data Format</h2><p class="mb-4" data-v-77759f09> Your CSV file should have the following format: </p><div class="bg-theme-tertiary p-4 rounded-lg font-mono text-sm" data-v-77759f09> x,y,label<br data-v-77759f09> 0.5,0.3,0<br data-v-77759f09> -0.2,0.8,1<br data-v-77759f09> 0.1,-0.4,0<br data-v-77759f09> ... </div><p class="mt-4" data-v-77759f09> Where <code data-v-77759f09>x</code> and <code data-v-77759f09>y</code> are coordinates in the range [-1, 1], and <code data-v-77759f09>label</code> is the class identifier (integer). </p></section><section class="mb-8" data-v-77759f09><h2 class="text-2xl font-bold text-theme-primary mb-4" data-v-77759f09>Similarity Metrics</h2><div class="space-y-4" data-v-77759f09><div data-v-77759f09><h3 class="font-semibold" data-v-77759f09>Dot Product</h3><p data-v-77759f09>Measures the alignment between neuron position and data point: x₁x₂ + y₁y₂</p></div><div data-v-77759f09><h3 class="font-semibold" data-v-77759f09>Euclidean Distance</h3><p data-v-77759f09>Measures the distance between neuron and data point: -√((x₁-x₂)² + (y₁-y₂)²)</p></div><div data-v-77759f09><h3 class="font-semibold" data-v-77759f09>My Product</h3><p data-v-77759f09>Normalized dot product: (x₁x₂ + y₁y₂) / √(distance² + ε)</p></div></div></section><section class="mb-8" data-v-77759f09><h2 class="text-2xl font-bold text-theme-primary mb-4" data-v-77759f09>Activation Functions</h2><div class="grid grid-cols-1 md:grid-cols-2 gap-4" data-v-77759f09><div class="bg-theme-secondary p-4 rounded-lg" data-v-77759f09><h3 class="font-semibold mb-2" data-v-77759f09>Softmax</h3><p class="text-sm" data-v-77759f09>Converts raw scores to probabilities that sum to 1</p></div><div class="bg-theme-secondary p-4 rounded-lg" data-v-77759f09><h3 class="font-semibold mb-2" data-v-77759f09>ReLU</h3><p class="text-sm" data-v-77759f09>Simple threshold function: max(0, x)</p></div><div class="bg-theme-secondary p-4 rounded-lg" data-v-77759f09><h3 class="font-semibold mb-2" data-v-77759f09>Sigmoid</h3><p class="text-sm" data-v-77759f09>S-shaped curve mapping to (0,1) range</p></div><div class="bg-theme-secondary p-4 rounded-lg" data-v-77759f09><h3 class="font-semibold mb-2" data-v-77759f09>GELU</h3><p class="text-sm" data-v-77759f09>Smooth ReLU alternative with Gaussian properties</p></div></div></section></div></div></main>',1))]))}});const m=n(r,[["__scopeId","data-v-77759f09"]]);export{m as default};
